Model:
 PETPT, Subroutine, J.T. Ritchie,  Calculates Priestly-Taylor potential evapotranspiration 
Kwang Soo Kim, luxkwang@snu.ac.kr 
Seoul National University, Seoul, Korea
DSSAT 4.7; original fortran code was written by J.T. Ritchie 
 See Document at DSSAT 4.7 source code in PETPT.for 













	double ALBEDO;
	double SLANG;
	double EEQ;
	double eo;
	double TD = 0.60*TMAX + 0.40*TMIN;
	if (XHLAI <= 0.0) {
		ALBEDO = MSALB;
	} else {
		ALBEDO = 0.23 - (0.23-MSALB)*Math.exp(-0.75*XHLAI);
	}
	SLANG = SRAD * 23.923;
	EEQ = SLANG * (2.04E-4 - 1.83E-4 * ALBEDO)*(TD+29.0);
	eo = EEQ*1.1;
	if (TMAX < 35.0) {
		eo = EEQ*((TMAX-35.0)*0.05+1.1);
	} else {
		eo = EEQ*0.01*Math.exp(0.18*(TMAX+20.0));
	}

	EO = Math.max(eo, 0.0001d);





	REAL ALBEDO, EEQ, SLANG, TD
	TD = 0.60*TMAX+0.40*TMIN
	IF (XHLAI .LE. 0.0) THEN
		ALBEDO = MSALB
	ELSE
		ALBEDO = 0.23-(0.23-MSALB)*EXP(-0.75*XHLAI)
	ENDIF
	SLANG = SRAD*23.923
	EEQ = SLANG*(2.04E-4-1.83E-4*ALBEDO)*(TD+29.0)
	EO = EEQ*1.1
	IF (TMAX .GT. 35.0) THEN
		EO = EEQ*((TMAX-35.0)*0.05+1.1)
	ELSE IF (TMAX .LT. 5.0) THEN
		EO = EEQ*0.01*EXP(0.18*(TMAX+20.0))
	ENDIF
	EO = MAX(EO,0.0001)


<Algorithm language=Cython" platform="" filename="PETPT.pyx" function="PETPT">

	cdef double ALBEDO, EEQ, SLANG, TD
	TD = 0.60*TMAX + 0.40*TMIN
	if (XHLAI <= 0.0):
		ALBEDO = MSALB
	else:
		ALBEDO = 0.23 - (0.23-MSALB)*exp(-0.75*XHLAI)
	SLANG = SRAD * 23.923
	EEQ = SLANG * (2.04E-4 - 1.83E-4 * ALBEDO)*(TD+29.0);
	eo = EEQ*1.1
	if (TMAX < 35.0):
		eo = EEQ*((TMAX-35.0)*0.05+1.1
	else:
		eo = EEQ*0.01*exp(0.18*(TMAX+20.0);
	EO = max(eo, 0.0001)


<Algorithm language=C#" platform="" filename="PETPT.cs" function="PETPT">

	double ALBEDO;
	double SLANG;
	double EEQ;
	double eo;
	double TD = 0.60*TMAX + 0.40*TMIN;
	if (XHLAI <= 0.0) {
		ALBEDO = MSALB;
	} else {
		ALBEDO = 0.23 - (0.23-MSALB)*Math.Exp(-0.75*XHLAI);
	}
	SLANG = SRAD * 23.923;
	EEQ = SLANG * (2.04E-4 - 1.83E-4 * ALBEDO)*(TD+29.0);
	eo = EEQ*1.1;
	if (TMAX < 35.0) {
		eo = EEQ*((TMAX-35.0)*0.05+1.1);
	} else {
		eo = EEQ*0.01*Math.Exp(0.18*(TMAX+20.0));
	}
	EO = Math.Max(eo, 0.0001d);
