ModelUnit.Algorithm:
    """
    if (iDoHarvest):
    	{
    	sDaysSinceSowing = 0
		sWLV = 0
		sWLVG = 0
		sWLVD = 0
		sWST = 0
		sWSO= 0
		sWRT= 0
		sLAI= 0

		rRWLVG= 0
		rRWRT= 0
		rDLAI= 0

		GTOTAL= 0
		PARINT= 0
		AGBM= 0

		rDeadLeaves= 0
		rDeadStems= 0
    	}

    if (iDoSow):
     {
        sDaysSinceSowing= 0

		sWLV= 0
		sWLVG= 0
		sWLVD= 0
		sWST= 0
		sWSO= 0
		sWRT= 0
		sLAI= 0
		rRWRT= 0

		rRWLVG= 0
		rRWRT= 0
		rDLAI= 0

		rDeadLeaves= 0
		rDeadStems= 0

		PARINT= 0
		AGBM= 0
    } 
    elif (RDRT_interpol.getXValues() == null)
    {
     return;
    }

   	tRLAI = 0.0
    tRWLVG = 0.0
     RWST = 0.0
    RWSO = 0.0
    tRWRT = 0.0
     DTR = float(RDD)
    PARINT_Neu = float(0.5 * DTR * (1.0 - exp(-cK * sLAI)))

    LueReductionToNStress = double(FSTFunctions.LIMIT(0, 1, 1 - cLueNStressReduction * pow(1.000 - iNitrogenNutritionIndex, 2)))

    if (iTRANRF &lt; LueReductionToNStress) {
        GTOTAL=iLUE* PARINT* iTRANRF;

    } else {
        GTOTAL=LUE * PARINT * LueReductionToNStress
    }
    GTOTAL=GTOTAL + iUnusedRootBiomass
    double DLV;
    double DLAI;
    if (iDevStage &gt; 0 &amp;&amp; sDaysSinceSowing &gt;= cRelativeDayOfEmergence) {
        double tSLA = cSLA * exp(-cSlaNStressReduction* (1 - iNitrogenNutritionIndex));
        double RDRDV = 0;
        if (iDevStage &gt; 1) {
            RDRDV = FSTFunctions.INSW(iDevStage - 1, 0, RDRT_interpol.getValueAt(DAVTMP.getValue()));
        }
        RDRSH = max(0, cRDRSHM * (sLAI - cLaiCritical) / cLaiCritical);
         RDRDRY = (1 - iTRANRF) * cRDRL
        RDR = max(max(RDRDV, RDRSH), RDRDRY) * iLeaveSenescenceHeatStressFactor
        DLVNS = 0;
        DLAINS = 0;
        if (iNitrogenNutritionIndex &lt; 1) {
            DLVNS = sWLVG * cRDRNS * (1 - iNitrogenNutritionIndex);
            DLAINS = DLVNS * tSLA;
        }
         DLVS = sWLVG * RDR;
         DLAIS = sLAI * RDR;
        DLV = DLVS + DLVNS;
        DLAI = DLAIS + DLAINS;
    } else {
        DLAI = 0d;
        DLV = 0d;
    }
    if (0 &lt; iDevStage &amp;&amp; iDevStage &lt; cGrainToRootsDevStage) {
        tRWRT = cGrainToRootsDailyBiomass + GTOTAL * FRT;
        RWST = GTOTAL * FST;
        RWSO = GTOTAL * FSO;
        tRWLVG = GTOTAL * FLV - DLV;
    } else {
        tRWRT = GTOTAL * FRT;
        RWST = GTOTAL * FST;
        RWSO = GTOTAL * FSO;
        tRWLVG = GTOTAL * FLV - DLV;
    }
    double GLV = FLV * GTOTAL;
    double tSLA = cSLA * exp(-cSlaNStressReduction * (1 - iNitrogenNutritionIndex))
    double GLAI = tSLA * GLV;
    if (iDevStage &lt; cDevStageRGRL &amp;&amp; sLAI &lt; 0.1875 * cLaiCritical) {
        GLAI = sLAI * (exp(cRGRL * DTEFF) - 1.0) * iTRANRF * exp(-cLaiNStressReduction * (1 - iNitrogenNutritionIndex))
    }
    if (iDevStage == 0) {
        GLAI = 0;
    } else if (sLAI == 0.0 &amp;&amp; WCA &gt; 0 &amp;&amp; sDaysSinceSowing &gt;= cRelativeDayOfEmergence) {
        GLAI = cLAII
    }
    tRLAI = GLAI - DLAI
    sDaysSinceSowing=sDaysSinceSowing + 1
    sLAI=sLAI + tRLAI
    sWLVG=sWLVG + tRWLVG
    sWLVD=sWLVD + DLV
    sWST=sWST + RWST
    sWSO=sWSO + RWSO
    sWRT=sWRT + tRWRT - iUnusedRootBiomass
    sWLV=sWLVG + sWLVD
    rRWLVG=tRWLVG
    rRWRT=tRWRT
    if (iDoSow &amp;&amp; rRWRT == 0.0)
        rRWRT=0.0001
    rDLAI=tRLAI
    rDeadLeaves=DLV
    if (iDevStage &gt; 1.5)
        rDeadStems=0.02 * sWST
    else
       rDeadStems=0.0
    AGBM=sWLV + sWST + sWSO
    PARINT=PARINT_Neu
    """
